def find_anagrams(s, p)
    p_len = p.length
    s_len = s.length
    return [] if p_len > s_len

    base = 97

    p_chars_count = Array.new(26, 0)
    for i in 0...p_len
        indx = p[i].ord - base
        p_chars_count[indx] += 1
    end

    res = []

    curr_chars_count = Array.new(26, 0)
    start = 0
    right = 0
    while right < (p_len - 1)
        indx = s[right].ord - base
        curr_chars_count[indx] += 1
        right += 1
    end

    for i in right...s_len
        indx = s[i].ord - base
        curr_chars_count[indx] += 1

        res << start if curr_chars_count == p_chars_count

        indx = s[start].ord - base
        curr_chars_count[indx] -= 1
        start += 1
    end
    res
end

for str1, str2 in [
    ["cbaebabacd", "abc"], # [0, 6]
    ["abab", "ab" ], # [0,1,2]
    ["aaaaaabaaaaaa", "aaaaaa"], # [0, 7]
    ["aaaaaaaaaa", "aaaaaaaaaaa"], # []
]
    puts find_anagrams(str1, str2).to_s
end

s1 = ""
s2 = ""

puts find_anagrams(s1, s2).to_s # [0, 10001]


